function Vue(options) {
    this._init(options)
    return
}
Vue.prototype._init = function(ops) {
    this.$options = ops
    initstate(this)
    if (ops.ele) {
        this.$mount(ops)
    }
}

function query(el) {
    if (typeof el == 'string') {
        return document.querySelector(el)
    } else {
        return {}
    }
}

Vue.prototype.$mount = function(ops) {
    let vm = this
    this.$el = query(ops.ele);
    vm._update();
}
Vue.prototype._update = function() {

    let node = document.createDocumentFragment()
    let firstChild;
    let ele = this.$el
    while (firstChild = ele.firstChild) {
        node.appendChild(firstChild)
    }
    compiler(node, this)
        // 此时已经 ele 已经没有子元素了
    ele.appendChild(node)

}

function compiler(node, vm) {
    let childNodes = node.childNodes;
    [...childNodes].forEach(function(e) {
        if (e.nodeType === 1) {
            compiler(e, vm)
        } else if (e.nodeType === 3) {
            compilertext(vm, e)
        }
    });
}

let WATCHER

function Watcher(vm, fnContent, cb) {
    if (typeof cb == 'function') {
        this._update = cb;
        this._update();
    }
    let getv = new Function('vm', fnContent)
    this.newValue = getv(vm)
    WATCHER = this
        //            this.getter()
}

function Dep() {
    this.subs = []
}

Dep.prototype.addsub = function(watcher) {
    this.subs.push(watcher)
}

Dep.prototype.notify = function() {
    this.subs.forEach(function(v) {
        v._update();
    })
}

function compilertext(vm, e) {
    const rexp = /\{\{((?:.|\r?\n)+?)\}\}/g;
    e.textContent = e.textContent.replace(rexp, function(...args) {
        return new Watcher(vm, `return vm.${args[1]}`, function() {
            vm._update()
        }).newValue
    })
}

function initstate(vm) {
    let opts = vm.$options
    if (opts.data) {
        initdata(vm)
    }
    if (opts.computed) {
        initcomputed()
    }
    if (opts.watch) {
        initwatch()
    }
}

function initdata(vm) {
    let data = vm.$options.data
    if (typeof data == 'function') {
        //data里面的this指向vm实例 this.xxx=xx
        vm._data = data.call(vm)
    } else {
        vm._data = data || {}
    }
    for (let k in vm._data) {
        proxy(vm, '_data', k)
    }
    observe(vm._data)
}

function proxy(vm, source, k) {
    Object.defineProperty(vm, k, {
        set(newv) {
            return vm[source][k] = newv
        },
        get() {
            return vm[source][k]
        }
    })
}

function observe(data) {
    if (typeof data == 'object') {
        observer(data)
    } else {
        return
    }
}

function observer(data) {
    let keys = Object.keys(data)
    if (Array.isArray(data)) {
        data.__proto__ = arrayMethods
        observearray(data)
    }
    keys.forEach(function(v) {
        reactive(data, v, data[v]);
    })
}

function reactive(d, k, v) {
    observe(v)
    let dep = new Dep()
        //Object.defineProperty(倾听的数据，倾听的键，配置{get,set})
    Object.defineProperty(d, k, {
        get() {
            if (WATCHER) {
                dep.addsub(WATCHER)
            }
            return v
        },
        set(newv) {
            if (newv === v) {
                return
            }
            observe(newv)
            dep.notify()
            return v = newv
        }
    })
}

function initwatch() {

}

function initcomputed() {

}

function observearray(args) {
    args.forEach(function(v) {
        observe(v)
    })
} //数组拦截

var oldMethods = Array.prototype
var arrayMethods = Object.create(oldMethods)
var methods = ['pop', 'push', 'shift', 'unshift', 'sort', 'splice', 'reverse'];
methods.forEach(function(method) {
    arrayMethods[method] = function(...args) {
        if (args.length > 0) {
            observearray(args)
        }
        return oldMethods[method].apply(this, args)
    }
})


module.export = Vue
